//******************************************************** M2: 1 user, 1 reader, multiple articles, multiple comments ***********************************

// add article for multiple articles in M2
//since we have only one user who is the owner of all articles, then we need to only have a 1-D array
void addart2(){
 if (articles[0]==0)  articles[0]=1;
else if(articles[1]==0)  articles[1]=1;
i++; // total number of articled is incearse by one
}
//remove an article
void delart2(){
articles[sa]=0; // delete the article
for(i=0;i<N;i++){ //delete all comments of that article
comments[sa][i]=0;  
}
i--; //total number of articled is decreased by one
numOfComInArt[sa]=0;
}
//add comment
void addcmt2(){
j=0;
while(j<N){
if (comments[sa][j]==0) {
                comments[sa][j]=cu+10;
                numOfComInArt[sa]++;
                j=2;// break the loop
}
j++;
}
}
 // del comment
void delcmt2(int c){
comments[sa][c]=0;
numOfComInArt[sa]--;
}

//******************************************************** M3: 1 ueser, 1 reader  , 1 article , multiple comments ***********************************

// add article for multiple articles in M3
//since we have 2 user who is the owner of all articles, then we need a 2-D array to see who is  owner of an article
void addart3(){
 if (article_array3[cu][0]==0)  article_array3[cu][0]=1;
else if(article_array3[cu][1]==0)  article_array3[cu][1]=1;
user_articles[cu]++; // total number of articled is incearse by one
}
//remove an article
void delart3(){
article_array3[cu][sa]=0; // delete the article
for(i=0;i<N;i++){ //delete all comments of that article
cmt_array3[cu][sa]=0;  
}
user_articles[cu]--; //total number of articled is decreased by one
numOfComInArt3[su][sa]=0;
}
//add comment
void addcmt3(){
j=0;
while(j<N){
if (cmt_array3[su][j]==0) {
                cmt_array3[su][j]=cu+10;
                numOfComInArt3[su][j]++;
                j=2;// break the loop
}
j++;
}
}
 // del comment
void delcmt3(int c){
cmt_array3[su][c]=0;
numOfComInArt3[su][c]--;
}

//******************************************************** M4: 2 users , multiple articles(each user 1 article), multiple comments ***********************************

